package Day5;

public class ShortHandOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//shorthand operators ---> it's just a shortcut
		
		int i=10;
		System.out.println("the score is "+ i);
		
		//i= i+5; //i=15
		i +=5;//15
		
		System.out.println("the score is " +i);
		
		//i=i-2; //i=13
		i -=2;//13
		
		System.out.println("the score is " + i);
		
		//i= i*10; //130
		i*=10;//130
		
		System.out.println("the score is "+i);
		
		//i= i/5; //26
		i/=5;//26
		
		System.out.println("the score is "+ i);
		
		//i=i%7; //5
		i%=7;//5
		
		System.out.println("the score is "+ i);
		
		//Example:
		 int n=20;
		 System.out.println("total is "+n);
		 
		 n +=5;//25
		 System.out.println("total is " +n);
		 
		 n -=5;//20
		 System.out.println("total is " +n);
		 
		 n /=2;//10
		 System.out.println("total is " +n);
		 
		 n %=3;//1
		 System.out.println("total is "+n);
		 
		 
		 
		 
		
		
		
		
		
		
		
		
		
		
		
	}

}
